macro.command:
// Roll current item
const roll = await item.roll();
if(!roll) return;

// Create rolls and push to chat
const FirstRoll = await new Roll("3d6").evaluate({ async: true });
const SecondRoll = await new Roll("1d6").evaluate({ async: true });
await FirstRoll.toMessage({ flavor: `${actor.name} loses spells with combined levels equal to or greater than:` });
await SecondRoll.toMessage({ flavor: `${actor.name} can't cast the chosen spells until finishing this many long rests:`});
