// Define names of effects to toggle
const effectNames = ["Rage", "Spirit of the Elk"];

const filter = token.actor.effects.filter(i => effectNames.includes(i.data.label));
const updates = filter.map(i => ({_id: i.id, disabled: !i.data.disabled}));
await token.actor.updateEmbeddedDocuments("ActiveEffect", updates);
